import React from 'react';

const App = () => {
  const [carrinho, setCarrinho] = React.useState(0);
  const [not, setNot] = React.useState(null);
  const timeoutRef = React.useRef();

  function handleClick() {
    setCarrinho(carrinho + 1);
    setNot('Itens adicionado ao carrinho!!');

    clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(() => {
      setNot(null);
    }, 1000);
  }

  return (
    <div>
      <p
        style={{
          background: '#5c2c0b',
          height: '40px',
          width: '40px',
          color: 'white',
          textAlign: 'center',
          marginTop: '100px',
        }}
      >
        {carrinho}
      </p>

      <p
        style={{
          color: 'green',
          background: 'orange',
          height: '100%',
          width: '100%',
        }}
      >
        {not}
      </p>

      <button onClick={handleClick}>Adicionar ao Carrinho</button>
    </div>
  );
};

export default App;
